// Blog.js
import React, { useState } from 'react';
import './App.css';

const Blog = () => {
  const [posts, setPosts] = useState([]);
  const [newPost, setNewPost] = useState({ title: '', author: '', content: '' });

  const handleAddPost = () => {
    if (newPost.title && newPost.author && newPost.content) {
      const date = new Date().toLocaleString();
      setPosts([...posts, { ...newPost, date, comments: [] }]);
      setNewPost({ title: '', author: '', content: '' });
    }
  };

  const handleAddComment = (index, comment) => {
    const updated = [...posts];
    updated[index].comments.push(comment);
    setPosts(updated);
  };

  return (
    <div className="content-box">
      <h2>Homework Help Forum</h2>
      <div style={{ marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="Title"
          value={newPost.title}
          onChange={(e) => setNewPost({ ...newPost, title: e.target.value })}
        />
        <input
          type="text"
          placeholder="Author"
          value={newPost.author}
          onChange={(e) => setNewPost({ ...newPost, author: e.target.value })}
        />
        <textarea
          placeholder="Write your question or post..."
          value={newPost.content}
          onChange={(e) => setNewPost({ ...newPost, content: e.target.value })}
        />
        <button onClick={handleAddPost}>Add Post</button>
      </div>

      {posts.map((post, i) => (
        <div
          key={i}
          className="content-box"
          style={{ marginBottom: '15px', padding: '15px' }}
        >
          <h3>{post.title}</h3>
          <p style={{ fontStyle: 'italic', fontSize: '14px' }}>
            by {post.author} â€¢ {post.date}
          </p>
          <p>{post.content}</p>

          <h4>Comments</h4>
          <ul style={{ listStyle: 'none', paddingLeft: 0 }}>
            {post.comments.map((c, j) => (
              <li
                key={j}
                style={{
                  background: '#f6f0fa',
                  padding: '8px',
                  borderRadius: '6px',
                  marginBottom: '5px',
                }}
              >
                {c}
              </li>
            ))}
          </ul>

          <CommentBox onAdd={(comment) => handleAddComment(i, comment)} />
        </div>
      ))}
    </div>
  );
};

const CommentBox = ({ onAdd }) => {
  const [comment, setComment] = useState('');
  return (
    <div style={{ marginTop: '10px' }}>
      <input
        type="text"
        placeholder="Write a comment..."
        value={comment}
        onChange={(e) => setComment(e.target.value)}
      />
      <button
        onClick={() => {
          if (comment.trim()) {
            onAdd(comment);
            setComment('');
          }
        }}
      >
        Add Comment
      </button>
    </div>
  );
};

export default Blog;
