// Schedule.js
import React, { useState } from 'react';
import './App.css';

const Schedule = () => {
  const [events, setEvents] = useState([]);
  const [newEvent, setNewEvent] = useState({ time: '', activity: '' });

  const addEvent = () => {
    if (newEvent.time && newEvent.activity) {
      setEvents([...events, newEvent]);
      setNewEvent({ time: '', activity: '' });
    }
  };

  const deleteEvent = (index) => {
    const updated = [...events];
    updated.splice(index, 1);
    setEvents(updated);
  };

  return (
    <div className="content-box">
      <h2>Daily Schedule</h2>
      <div style={{ display: 'flex', marginBottom: '15px' }}>
        <input
          type="time"
          value={newEvent.time}
          onChange={(e) => setNewEvent({ ...newEvent, time: e.target.value })}
        />
        <input
          type="text"
          placeholder="Enter activity..."
          value={newEvent.activity}
          onChange={(e) => setNewEvent({ ...newEvent, activity: e.target.value })}
        />
        <button onClick={addEvent}>Add</button>
      </div>

      <ul style={{ listStyle: 'none', padding: 0 }}>
        {events.map((event, i) => (
          <li
            key={i}
            style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              padding: '10px',
              borderBottom: '1px solid #ddd',
            }}
          >
            <span>
              <strong>{event.time}</strong> â€” {event.activity}
            </span>
            <button
              onClick={() => deleteEvent(i)}
              style={{
                background: '#ff8a65',
                padding: '6px 10px',
                fontSize: '13px',
              }}
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Schedule;
